function renderCurrenciesPagination(totalItems) {
    const paginationContainer = document.getElementById('currenciesPagination');
    if (!paginationContainer) return;

    paginationContainer.innerHTML = '';
    const totalPages = Math.ceil(totalItems / paginationConfig.currenciesPerPage);

    const prevBtn = document.createElement('button');
    prevBtn.innerHTML = '<i class="fas fa-chevron-left"></i>';
    prevBtn.disabled = currenciesCurrentPage === 1;
    prevBtn.addEventListener('click', () => {
        if (currenciesCurrentPage > 1) {
            currenciesCurrentPage--;
            renderExchangeRates();
        }
    });

    const nextBtn = document.createElement('button');
    nextBtn.innerHTML = '<i class="fas fa-chevron-right"></i>';
    nextBtn.disabled = currenciesCurrentPage === totalPages;
    nextBtn.addEventListener('click', () => {
        if (currenciesCurrentPage < totalPages) {
            currenciesCurrentPage++;
            renderExchangeRates();
        }
    });

    const pageInfo = document.createElement('span');
    pageInfo.textContent = `Strona ${currenciesCurrentPage} z ${totalPages}`;

    paginationContainer.appendChild(prevBtn);
    paginationContainer.appendChild(pageInfo);
    paginationContainer.appendChild(nextBtn);
}
