function renderExchangeRates() {
    const container = document.getElementById('customCurrencies');
    container.innerHTML = '';

    const baseCurrencies = ['PLN', 'USD', 'EUR'];
    const nbpCurrencies = Object.keys(exchangeRates)
        .filter(c => c !== 'PLN' && c !== 'USD' && c !== 'EUR' && c !== 'lastUpdated')
        .sort();

    const allCurrencies = [...baseCurrencies, ...nbpCurrencies];
    const startIndex = (currenciesCurrentPage - 1) * paginationConfig.currenciesPerPage;
    const endIndex = Math.min(startIndex + paginationConfig.currenciesPerPage, allCurrencies.length);

    allCurrencies.slice(startIndex, endIndex).forEach(code => {
        const currencyItem = createCurrencyItem(code, true);
        container.appendChild(currencyItem);
