function calculateCategoryEfficiency() {
  const now = new Date();
  const efficiencyData = {};
  categories.forEach(category => {
    const catName = category.name;
    const catProducts = products.filter(p => p.category === catName);
    if (catProducts.length === 0) return;
    let totalPurchase = 0;
    let totalCurrent = 0;
    let totalDays = 0;
    let totalProfit = 0;
    let totalLoss = 0;
    let productsWithPrice = 0;
    catProducts.forEach(p => {
      const purchasePLN = convertToPLN(p.price, p.currency);
      totalPurchase += p.quantity * purchasePLN;
      if (p.currentPrice !== null && p.currentPrice !== undefined) {
        const currentPLN = convertToPLN(p.currentPrice, p.currentCurrency || p.currency);
        totalCurrent += p.quantity * currentPLN;
        const diff = (currentPLN - purchasePLN) * p.quantity;
        if (diff > 0) totalProfit += diff;
        else if (diff < 0) totalLoss += Math.abs(diff);
        productsWithPrice++;
      }
      const addedDate = new Date(p.dateAdded);
      const daysInStorage = Math.floor((now - addedDate) / (1000 * 60 * 60 * 24));
      totalDays += daysInStorage;
    });
    const diff = totalCurrent - totalPurchase;
    const diffPercent = totalPurchase > 0 ? (diff / totalPurchase) * 100 : 0;
    const avgDays = totalDays / catProducts.length;
    const roi = totalPurchase > 0 ? (diff / totalPurchase) * 100 : 0;
    efficiencyData[catName] = {
      totalPurchase,
      totalCurrent,
      diff,
      diffPercent,
      avgDays,
      roi,
      productCount: catProducts.length,
      productsWithPrice,
      profit: totalProfit,
      loss: totalLoss
    };
  });
  return efficiencyData;
}
function calculateRiskMetrics() {
    const targetCurrency = currencyConfig.extendedStatsCurrency;

    let maxLoss = 0;
    products.forEach(p => {
        const purchasePLN = convertToPLN(p.price, p.currency);
        const currentPLN = p.currentPrice ? 
            convertToPLN(p.currentPrice, p.currentCurrency || p.currency) : purchasePLN;
        const potentialLoss = (purchasePLN - currentPLN) * p.quantity;
        if (potentialLoss > maxLoss) maxLoss = potentialLoss;
    });

    const avgReturn = parseFloat(profitPercentage.textContent);
    const riskFreeRate = 0.05;
    const volatility = 15;
    const sharpeRatio = ((avgReturn - riskFreeRate) / volatility).toFixed(2);

    const convertedMaxLoss = convertToCurrency(maxLoss, 'PLN', targetCurrency);

    document.getElementById('varValue').textContent = formatCurrency(convertedMaxLoss, targetCurrency, true, 'extendedStats');
    document.getElementById('sharpeRatio').textContent = sharpeRatio;

    renderDensityChart();
}

function calculateLiquidityMetrics() {
  const now = new Date();
  let totalDays = 0;
  products.forEach(p => {
    const addedDate = new Date(p.dateAdded);
    const daysInStorage = Math.floor((now - addedDate) / (1000 * 60 * 60 * 24));
    totalDays += daysInStorage;
  });
  const avgDays = products.length > 0 ? Math.floor(totalDays / products.length) : 0;
  document.getElementById('avgStorageTime').textContent = avgDays + ' dni';
  const inventoryTurnover = avgDays > 0 ? (365 / avgDays).toFixed(1) : 0;
  document.getElementById('inventoryTurnover').textContent = inventoryTurnover;
  renderLiquidityHeatmap();
}
