function calculatePortfolioMetrics() {
  let hhi = 0;
  const categoryValues = {};
  let totalValue = 0;
  categories.forEach(cat => {
    const catProducts = products.filter(p => p.category === cat.name);
    const catValue = catProducts.reduce((sum, p) => {
      const currentPLN = p.currentPrice ? 
        convertToPLN(p.currentPrice, p.currentCurrency || p.currency) : 
        convertToPLN(p.price, p.currency);
      return sum + (currentPLN * p.quantity);
    }, 0);
    categoryValues[cat.name] = catValue;
    totalValue += catValue;
  });
  for (const cat in categoryValues) {
    const marketShare = (categoryValues[cat] / totalValue) * 100;
    hhi += marketShare * marketShare;
  }
  document.getElementById('diversificationIndex').textContent = hhi.toFixed(0);
  renderEfficientFrontier();
}
function calculateForecasts() {
  const growthRates = [];
  const now = new Date();
  products.forEach(p => {
    if (p.priceHistory && p.priceHistory.length > 1) {
      const firstPrice = convertToPLN(p.priceHistory[0].price, p.priceHistory[0].currency);
      const lastPrice = convertToPLN(p.priceHistory[p.priceHistory.length - 1].price, 
                                    p.priceHistory[p.priceHistory.length - 1].currency);
      const months = (new Date(p.priceHistory[p.priceHistory.length - 1].date) - 
                     new Date(p.priceHistory[0].date)) / (1000 * 60 * 60 * 24 * 30);
      if (months > 0) {
        const monthlyGrowth = Math.pow(lastPrice / firstPrice, 1/months) - 1;
        growthRates.push(monthlyGrowth);
      }
    }
  });
  const avgGrowth = growthRates.length > 0 ? 
    growthRates.reduce((sum, rate) => sum + rate, 0) / growthRates.length : 
    0.01;
  const currentValue = parseFloat(document.getElementById('currentValue').textContent.replace(' zł', '').replace(',', ''));
  const sixMonthForecast = currentValue * Math.pow(1 + avgGrowth, 6);
  document.getElementById('sixMonthForecast').textContent = sixMonthForecast.toFixed(2).replace('.', ',') + ' zł';
  renderForecastChart(avgGrowth);
}
function generateCorrelationMatrix() {
  const matrixContainer = document.getElementById('correlationMatrix');
  matrixContainer.innerHTML = '';
  const categoriesList = categories.map(c => c.name);
  const matrix = [];
  categoriesList.forEach((cat1, i) => {
    matrix[i] = [];
    categoriesList.forEach((cat2, j) => {
      if (i === j) {
        matrix[i][j] = 1.0;
      } else {
        matrix[i][j] = (Math.random() * 2 - 1).toFixed(2);
      }
    });
  });
  const headerRow = document.createElement('div');
  headerRow.className = 'matrix-row';
  headerRow.innerHTML = '<div class="matrix-cell"></div>';
  categoriesList.forEach(cat => {
    headerRow.innerHTML += `<div class="matrix-cell header">${cat}</div>`;
  });
  matrixContainer.appendChild(headerRow);
  categoriesList.forEach((cat1, i) => {
    const row = document.createElement('div');
    row.className = 'matrix-row';
    row.innerHTML = `<div class="matrix-cell header">${cat1}</div>`;
    categoriesList.forEach((cat2, j) => {
      const correlation = matrix[i][j];
      const absValue = Math.abs(correlation);
      const hue = correlation > 0 ? 120 : 0;
      const lightness = 90 - (absValue * 40);
      const bgColor = `hsl(${hue}, 80%, ${lightness}%)`;
      row.innerHTML += `
        <div class="matrix-cell" style="background-color: ${bgColor};">
          ${correlation}
        </div>
      `;
    });
    matrixContainer.appendChild(row);
  });
}
function renderDensityChart() {
  const ctx = document.getElementById('densityChart').getContext('2d');
  const returns = [];
  products.forEach(p => {
    if (p.currentPrice) {
      const purchasePLN = convertToPLN(p.price, p.currency);
      const currentPLN = convertToPLN(p.currentPrice, p.currentCurrency || p.currency);
      const returnRate = ((currentPLN - purchasePLN) / purchasePLN) * 100;
      returns.push(returnRate);
    }
  });
  const bins = {};
  returns.forEach(ret => {
    const bin = Math.floor(ret / 10) * 10;
    bins[bin] = (bins[bin] || 0) + 1;
  });
  const labels = [];
  const data = [];
  for (let i = -50; i <= 100; i += 10) {
    labels.push(`${i}% - ${i+10}%`);
    data.push(bins[i] || 0);
  }
  new Chart(ctx, {
    type: 'bar',
    data: {
      labels: labels,
      datasets: [{
        label: 'Liczba produktów',
        data: data,
        backgroundColor: 'rgba(77, 182, 172, 0.7)'
      }]
    },
    options: {
      responsive: true,
      plugins: {
        title: {
          display: true,
          text: 'Rozkład stóp zwrotu'
        }
      },
      scales: {
        y: {
          beginAtZero: true,
          title: {
            display: true,
            text: 'Liczba produktów'
          }
        },
        x: {
          title: {
            display: true,
            text: 'Stopa zwrotu'
          }
        }
      }
    }
  });
}
