function renderSankeyChart() {
  const ctx = document.getElementById('sankeyChart').getContext('2d');
  const categoryProfit = {};
  const categoryLoss = {};
  products.forEach(p => {
    if (p.currentPrice) {
      const purchasePLN = convertToPLN(p.price, p.currency);
      const currentPLN = convertToPLN(p.currentPrice, p.currentCurrency || p.currency);
      const diff = (currentPLN - purchasePLN) * p.quantity;
      if (diff > 0) {
        categoryProfit[p.category] = (categoryProfit[p.category] || 0) + diff;
      } else {
        categoryLoss[p.category] = (categoryLoss[p.category] || 0) + Math.abs(diff);
      }
    }
  });
  const nodes = [];
  const links = [];
  Object.keys(categoryProfit).forEach(category => {
    nodes.push({name: category});
  });
  nodes.push({name: 'Zysk'});
  nodes.push({name: 'Strata'});
  Object.keys(categoryProfit).forEach(category => {
    const categoryIndex = nodes.findIndex(n => n.name === category);
    const profitIndex = nodes.findIndex(n => n.name === 'Zysk');

    links.push({
      source: categoryIndex,
      target: profitIndex,
      value: categoryProfit[category]
    });
  });

  Object.keys(categoryLoss).forEach(category => {
    const categoryIndex = nodes.findIndex(n => n.name === category);
    const lossIndex = nodes.findIndex(n => n.name === 'Strata');

    links.push({
      source: categoryIndex,
      target: lossIndex,
      value: categoryLoss[category]
    });
  });
  new Chart(ctx, {
    type: 'sankey',
    data: {
      datasets: [{
        data: nodes,
        links: links,
        colorFrom: (c) => getCategoryColor(nodes[c.dataset.data[c.dataIndex].name].name).bg,
        colorTo: (c) => getCategoryColor(nodes[c.dataset.data[c.dataIndex].name].name).bg,
      }]
    },
    options: {
      responsive: true,
      plugins: {
        title: {
          display: true,
          text: 'Przepływ wartości między kategoriami'
        }
      }
    }
  });
}
