function renderTreemap() {
  const ctx = document.getElementById('treemapCanvas').getContext('2d');
  const treeData = {
    name: 'Magazyn',
    value: parseFloat(document.getElementById('currentValue').textContent.replace(' zł', '').replace(',', '')),
    children: []
  };
  categories.forEach(category => {
    const categoryProducts = products.filter(p => p.category === category.name);
    const categoryValue = categoryProducts.reduce((sum, p) => {
      const currentPLN = p.currentPrice ? 
        convertToPLN(p.currentPrice, p.currentCurrency || p.currency) : 
        convertToPLN(p.price, p.currency);
      return sum + (currentPLN * p.quantity);
    }, 0);

    if (categoryValue > 0) {
      const categoryNode = {
        name: category.name,
        value: categoryValue,
        children: []
      };
      categoryProducts.forEach(p => {
        const currentPLN = p.currentPrice ? 
          convertToPLN(p.currentPrice, p.currentCurrency || p.currency) : 
          convertToPLN(p.price, p.currency);
        const productValue = currentPLN * p.quantity;

        if (productValue > categoryValue * 0.05) {
          categoryNode.children.push({
            name: p.name,
            value: productValue
          });
        }
      });

      treeData.children.push(categoryNode);
    }
  });
  new Chart(ctx, {
    type: 'treemap',
    data: {
      datasets: [{
        tree: treeData,
        key: 'value',
        labels: {
          display: true,
          formatter: (ctx) => ctx.raw.name
        },
        backgroundColor: (ctx) => {
          if (ctx.type === 'data') {
            return getCategoryColor(ctx.raw.name).bg || '#4DB6AC';
          }
          return '#f0f0f0';
        },
        color: (ctx) => {
          if (ctx.type === 'data') {
            return getCategoryColor(ctx.raw.name).text || '#333';
          }
          return '#333';
        },
        font: {
          size: (ctx) => ctx.type === 'data' ? 12 : 14
        }
      }]
    },
    options: {
      plugins: {
        title: {
          display: true,
          text: 'Struktura wartości produktów'
        },
        tooltip: {
          callbacks: {
            title: (items) => items[0].raw.name,
            label: (item) => `Wartość: ${item.raw.value.toFixed(2)} zł`
          }
        }
      }
    }
  });
}
