async function runMonteCarlo() {
    const targetCurrency = currencyConfig.extendedStatsCurrency;

    const resultsContainer = document.getElementById('monteCarloResults');
    resultsContainer.innerHTML = '<div class="simulation-loading"><i class="fas fa-spinner fa-spin"></i> Trwa symulacja...</div>';

    try {
        const returns = [];
        const now = new Date();
        const historyPromises = products.map(p => priceHistory.getPriceHistory(p.id));
        const allHistories = await Promise.all(historyPromises);

        for (let i = 0; i < products.length; i++) {
            const history = allHistories[i];
            if (history.length < 2) continue;

            const firstEntry = history[0];
            const lastEntry = history[history.length - 1];
            const firstDate = new Date(firstEntry.date);
            const lastDate = new Date(lastEntry.date);
            const yearsDiff = (lastDate - firstDate) / (1000 * 60 * 60 * 24 * 365.25);

            if (yearsDiff < 0.08) continue;

            const firstPrice = convertToPLN(firstEntry.price, firstEntry.currency);
            const lastPrice = convertToPLN(lastEntry.price, lastEntry.currency);

            if (firstPrice <= 0 || lastPrice <= 0) continue;
            if (firstDate > lastDate) continue;

            const logReturn = Math.log(lastPrice / firstPrice);
            const annualReturn = logReturn / yearsDiff;

            if (Math.abs(annualReturn) < 1) {
                returns.push(annualReturn);
            }
        }

        let avgReturn = 0.05;
        let stdDev = 0.15;

        if (returns.length > 0) {
            returns.sort((a, b) => a - b);
            const trimCount = Math.floor(returns.length * 0.1);
            const trimmedReturns = returns.slice(trimCount, returns.length - trimCount);

            avgReturn = trimmedReturns.reduce((sum, r) => sum + r, 0) / trimmedReturns.length;

            if (trimmedReturns.length > 1) {
                const squaredDiffs = trimmedReturns.map(r => Math.pow(r - avgReturn, 2));
                stdDev = Math.sqrt(squaredDiffs.reduce((sum, sd) => sum + sd, 0) / (trimmedReturns.length - 1));
            }

            avgReturn = Math.max(Math.min(avgReturn, 0.5), -0.5);
            stdDev = Math.min(stdDev, 0.5);
        }

        const currentValue = parseFloat(
            document.getElementById('currentValue').textContent
                .replace(' zł', '')
                .replace(/\s/g, '')
                .replace(',', '.')
        );

        const years = [1, 3, 5, 10];
        let scenariosHTML = '';

        years.forEach(year => {
            const simulations = [];
            for (let i = 0; i < 1000; i++) {
                let portfolioValue = currentValue;
                for (let y = 0; y < year; y++) {
                    const growth = avgReturn + stdDev * gaussianRandom();
                    portfolioValue *= (1 + growth);
                }
                simulations.push(portfolioValue);
            }
            simulations.sort((a, b) => a - b);
            const p10 = simulations[100];
            const median = simulations[500];
            const p90 = simulations[900];
            const convertedP10 = convertToCurrency(p10, 'PLN', targetCurrency);
            const convertedMedian = convertToCurrency(median, 'PLN', targetCurrency);
            const convertedP90 = convertToCurrency(p90, 'PLN', targetCurrency);

            scenariosHTML += `
                <div class="simulation-scenario">
                    <span>Po ${year} ${year === 1 ? 'roku' : 'latach'}:</span>
                    <div class="simulation-range">
                        <span class="value-min">${formatCurrency(convertedP10, targetCurrency, true, 'extendedStats')}</span>
                        <span class="value-median">${formatCurrency(convertedMedian, targetCurrency, true, 'extendedStats')}</span>
                        <span class="value-max">${formatCurrency(convertedP90, targetCurrency, true, 'extendedStats')}</span>
                    </div>
                    <div class="simulation-bar">
                        <div class="simulation-range-bar" style="width: 80%; left: 10%;"></div>
                    </div>
                    <div class="simulation-labels">
                        <span>10%</span>
                        <span>Mediana</span>
                        <span>90%</span>
                    </div>
                </div>
            `;
        });

        const convertedCurrentValue = convertToCurrency(currentValue, 'PLN', targetCurrency);

        resultsContainer.innerHTML = `
            <h4>Symulacja Monte Carlo (${returns.length}/${products.length} produktów)</h4>
            ${scenariosHTML}
            <div class="simulation-note">
                Średni zwrot: ${(avgReturn * 100).toFixed(2)}% | 
                Ryzyko: ${(stdDev * 100).toFixed(2)}% |
                Wartość początkowa: ${formatCurrency(convertedCurrentValue, targetCurrency, true, 'extendedStats')}
            </div>
        `;

    } catch (error) {
        resultsContainer.innerHTML = '<div class="error">Błąd podczas symulacji</div>';
    }
}
function formatNumber(value) {
    if (isNaN(value)) return "0 zł";
    if (value < 1000) return value.toFixed(2) + " zł";
    if (value < 1000000) return (value/1000).toFixed(1) + " tys. zł";
    return (value/1000000).toFixed(1) + " mln zł";
}
function gaussianRandom() {
    let u = 0, v = 0;
    while(u === 0) u = Math.random();
    while(v === 0) v = Math.random();
    return Math.sqrt(-2.0 * Math.log(u)) * Math.cos(2.0 * Math.PI * v);
}
function showExtendedStats() {
    const modal = document.getElementById('statsModal');
    const content = document.getElementById('extendedStatsContent');
    const targetCurrency = currencyConfig.extendedStatsCurrency;

    let totalValue = 0;
    let purchaseValue = 0;
    let currentValue = 0;
    let profitValue = 0;
    let lossValue = 0;
    let productsWithCurrentPrice = 0;
    let profitProducts = 0;
    let lossProducts = 0;
    let neutralProducts = 0;
    const valueByCurrency = {};
    const valueByYear = {};
    const valueByCategory = {};
    const profitDistribution = {
        highProfit: 0,
        mediumProfit: 0,
        lowProfit: 0,
        noChange: 0,
        lowLoss: 0,
        mediumLoss: 0,
        highLoss: 0
    };

    products.forEach(p => {
        const purchasePLN = convertToPLN(p.price, p.currency);
        const totalPurchase = p.quantity * purchasePLN;
        const currentPrice = p.currentPrice !== null && p.currentPrice !== undefined ? 
            convertToPLN(p.currentPrice, p.currentCurrency || p.currency) : 
            purchasePLN;
        const totalCurrent = p.quantity * currentPrice;

        totalValue += totalCurrent;
        purchaseValue += totalPurchase;
        currentValue += totalCurrent;

        if (!valueByCurrency[p.currency]) {
            valueByCurrency[p.currency] = 0;
        }
        valueByCurrency[p.currency] += totalPurchase;

        if (!valueByYear[p.year]) {
            valueByYear[p.year] = 0;
        }
        valueByYear[p.year] += totalPurchase;

        if (!valueByCategory[p.category]) {
            valueByCategory[p.category] = 0;
        }
        valueByCategory[p.category] += totalPurchase;

        if (p.currentPrice !== null && p.currentPrice !== undefined) {
            productsWithCurrentPrice++;
            const diff = (currentPrice - purchasePLN) * p.quantity;
            if (diff > 0) {
                profitValue += diff;
                profitProducts++;
                const profitPercent = (diff / (purchasePLN * p.quantity)) * 100;
                if (profitPercent > 50) profitDistribution.highProfit++;
                else if (profitPercent > 20) profitDistribution.mediumProfit++;
                else profitDistribution.lowProfit++;
            } else if (diff < 0) {
                lossValue += Math.abs(diff);
                lossProducts++;
                const lossPercent = (Math.abs(diff) / (purchasePLN * p.quantity)) * 100;
                if (lossPercent > 50) profitDistribution.highLoss++;
                else if (lossPercent > 20) profitDistribution.mediumLoss++;
                else profitDistribution.lowLoss++;
            } else {
                neutralProducts++;
                profitDistribution.noChange++;
            }
        }
    });

    const diff = currentValue - purchaseValue;

    const topProfitValue = [];
    const topLossValue = [];
    const topProfitPercent = [];
    const topLossPercent = [];

    products.forEach(p => {
        if (p.currentPrice !== null && p.currentPrice !== undefined) {
            const purchasePLN = convertToPLN(p.price, p.currency);
            const currentPLN = convertToPLN(p.currentPrice, p.currentCurrency || p.currency);
            const diff = (currentPLN - purchasePLN) * p.quantity;
            const diffPercent = purchasePLN > 0 ? ((currentPLN - purchasePLN) / purchasePLN) * 100 : 0;

            if (diff > 0) {
                topProfitValue.push({...p, diff});
                topProfitPercent.push({...p, diffPercent});
            } else if (diff < 0) {
                topLossValue.push({...p, diff: Math.abs(diff)});
                topLossPercent.push({...p, diffPercent: Math.abs(diffPercent)});
            }
        }
    });

    topProfitValue.sort((a, b) => b.diff - a.diff).splice(5);
    topLossValue.sort((a, b) => b.diff - a.diff).splice(5);
    topProfitPercent.sort((a, b) => b.diffPercent - a.diffPercent).splice(10);
    topLossPercent.sort((a, b) => b.diffPercent - a.diffPercent).splice(10);

    const productsByCategory = {};
    products.forEach(p => {
        productsByCategory[p.category] = (productsByCategory[p.category] || 0) + 1;
    });

    const now = new Date();
    let totalDays = 0;
    const oldestProducts = [];
    products.forEach(p => {
        const addedDate = new Date(p.dateAdded);
        const daysInStorage = Math.floor((now - addedDate) / (1000 * 60 * 60 * 24));
        totalDays += daysInStorage;
        oldestProducts.push({...p, days: daysInStorage});
    });

    const avgDays = products.length > 0 ? Math.floor(totalDays / products.length) : 0;
    const avgYears = (avgDays / 365).toFixed(1);

    let totalPriceRatio = 0;
    let productsWithPrices = 0;
    const missingPrices = [];
    const categoryPrices = {};

    products.forEach(p => {
        if (p.currentPrice !== null && p.currentPrice !== undefined) {
            const purchasePLN = convertToPLN(p.price, p.currency);
            const currentPLN = convertToPLN(p.currentPrice, p.currentCurrency || p.currency);
            const ratio = (currentPLN / purchasePLN) * 100;
            totalPriceRatio += ratio;
            productsWithPrices++;

            if (!categoryPrices[p.category]) {
                categoryPrices[p.category] = { totalPurchase: 0, totalCurrent: 0, count: 0 };
            }
            categoryPrices[p.category].totalPurchase += purchasePLN;
            categoryPrices[p.category].totalCurrent += currentPLN;
            categoryPrices[p.category].count++;
        } else {
            missingPrices.push(p);
        }
    });

    const avgPriceRatio = productsWithPrices > 0 ? (totalPriceRatio / productsWithPrices).toFixed(1) : 0;

    content.innerHTML = `
        <div class="extended-stats-grid">
            <div class="currency-info">
                <h3><i class="fas fa-coins"></i> Podstawowe informacje</h3>
                <div class="currency-info-item">
                    <span>Wartość zakupu produktów:</span>
                    <span class="currency-value">${formatCurrency(purchaseValue, 'PLN', true, 'extendedStats')}</span>
                </div>
                <div class="currency-info-item">
                    <span>Aktualna wartość całego magazynu:</span>
                    <span class="currency-value">${formatCurrency(currentValue, 'PLN', true, 'extendedStats')}</span>
                </div>
                <div class="currency-info-item">
                    <span>Różnica wartości między aktualną ceną a tym za ile zakupiłem:</span>
                    <span class="currency-value ${diff > 0 ? 'profit' : diff < 0 ? 'loss' : ''}">
                        ${formatCurrency(diff, 'PLN', true, 'extendedStats')}
                    </span>
                </div>
                <div class="currency-info-item">
                    <span>Ilość produktów:</span>
                    <span class="currency-value">${products.length}</span>
                </div>
