function getCategoryColor(categoryName) {
  const category = categories.find(c => c.name === categoryName);
  return category ? { bg: category.bg, text: category.text } : { bg: '#ededed', text: '#333' };
}
async function initializeExchangeRates() {
    if (!exchangeRates.lastUpdated) {
        await fetchExchangeRates();
    } else {
        const lastUpdated = new Date(exchangeRates.lastUpdated);
        const now = new Date();
        const hoursSinceLastUpdate = (now - lastUpdated) / (1000 * 60 * 60);
        if (hoursSinceLastUpdate > 1) {
            await fetchExchangeRates();
        }
    }
    if (!exchangeRates.GOLD) {
        await fetchGoldPrice();
    }
}
