function showSoundPermissionModal() {
  const savedPermission = localStorage.getItem('soundPermission');
  if (savedPermission !== null) {
    soundPermissionGiven = savedPermission === 'true';
    return;
  }
  const modal = document.getElementById('soundPermissionModal');
  modal.classList.add('show');


  setTimeout(() => {
    const content = modal.querySelector('.modal-content');
    const scrollBtn = modal.querySelector('.modal-scroll-to-top');
    if (content) content.scrollTop = 0;
    if (scrollBtn) scrollBtn.classList.remove('show');
  }, 100);
  document.getElementById('soundPermissionAccept').onclick = () => {
    soundPermissionGiven = true;
    soundAllowed = true;
    localStorage.setItem('soundPermission', 'true');
    modal.classList.remove('show');
    showNotification('Dźwięki zostały włączone', 'success');
	playNotificationSound('success');
  };
  document.getElementById('soundPermissionReject').onclick = () => {
    soundPermissionGiven = false;
    soundAllowed = false;
    localStorage.setItem('soundPermission', 'false');
    modal.classList.remove('show');
    showNotification('Dźwięki zostały wyłączone', 'info');
  };
  document.getElementById('playSuccessSound').onclick = () => {
    playDemoSound('success');
  };
  document.getElementById('playAlertSound').onclick = () => {
    playDemoSound('alert');
  };
}
function generateNewStatistics() {
  const now = new Date();
  let totalDays = 0;
  const oldestProducts = [];
  const newestProducts = [];
  products.forEach(p => {
    const addedDate = new Date(p.dateAdded);
    const daysInStorage = Math.floor((now - addedDate) / (1000 * 60 * 60 * 24));
    totalDays += daysInStorage;
    oldestProducts.push({...p, days: daysInStorage});
    if (daysInStorage <= 30) {
      newestProducts.push({...p, days: daysInStorage});
    }
  });
  const avgDays = products.length > 0 ? Math.floor(totalDays / products.length) : 0;
  const avgYears = (avgDays / 365).toFixed(1);
  const setTextIfExists = (elementId, text) => {
    const el = document.getElementById(elementId);
    if (el) el.textContent = text;
  };
  setTextIfExists('avgStorageTime', `${avgYears} lat`);
  setTextIfExists('recentProducts', newestProducts.length);
  const heatmap = document.getElementById('activityHeatmap');
  if (heatmap) {
    generateActivityHeatmap();
  }
  const oldestList = document.getElementById('oldestProducts');
  if (oldestList) {
    oldestList.innerHTML = '';
    oldestProducts.sort((a, b) => b.days - a.days).slice(0, 5).forEach(p => {
      const years = (p.days / 365).toFixed(1);
      const item = document.createElement('div');
      item.className = 'old-product-item';
      item.innerHTML = `
        <span class="old-product-name">${p.name}</span>
        <span class="old-product-years">${years} lat</span>
      `;
      oldestList.appendChild(item);
    });
  }
  const newestList = document.getElementById('newestProducts');
  if (newestList) {
    newestList.innerHTML = '';
    newestProducts.slice(0, 5).forEach(p => {
      const item = document.createElement('div');
      item.className = 'old-product-item';
      item.innerHTML = `
        <span class="old-product-name">${p.name}</span>
        <span class="old-product-years">${p.days} dni</span>
      `;
      newestList.appendChild(item);
    });
  }
  let totalPriceRatio = 0;
  let productsWithPrices = 0;
  const missingPrices = [];
  const categoryPrices = {};
  products.forEach(p => {
    if (p.currentPrice !== null && p.currentPrice !== undefined) {
      const purchasePLN = convertToPLN(p.price, p.currency);
      const currentPLN = convertToPLN(p.currentPrice, p.currentCurrency || p.currency);
      const ratio = (currentPLN / purchasePLN) * 100;
      totalPriceRatio += ratio;
      productsWithPrices++;
      if (!categoryPrices[p.category]) {
        categoryPrices[p.category] = { totalPurchase: 0, totalCurrent: 0, count: 0 };
      }
      categoryPrices[p.category].totalPurchase += purchasePLN;
      categoryPrices[p.category].totalCurrent += currentPLN;
      categoryPrices[p.category].count++;
    } else {
      missingPrices.push(p);
    }
  });
  const avgPriceRatio = productsWithPrices > 0 ? (totalPriceRatio / productsWithPrices).toFixed(1) : 0;
  setTextIfExists('avgPriceRatio', `${avgPriceRatio}%`);
  setTextIfExists('missingPricesCount', missingPrices.length);
  const missingList = document.getElementById('missingPricesList');
  if (missingList) {
    missingList.innerHTML = '';
    missingPrices.slice(0, 10).forEach(p => {
      const item = document.createElement('div');
      item.className = 'missing-price-item';
      item.innerHTML = `
        <span class="missing-price-name">${p.name}</span>
        <span class="missing-price-category">${p.category}</span>
      `;
      missingList.appendChild(item);
    });
  }
  const categoryList = document.getElementById('categoryPrices');
  if (categoryList) {
    categoryList.innerHTML = '';
    for (const [category, data] of Object.entries(categoryPrices)) {
      const avgPurchase = data.totalPurchase / data.count;
      const avgCurrent = data.totalCurrent / data.count;
      const ratio = ((avgCurrent / avgPurchase) * 100).toFixed(1);

      const item = document.createElement('div');
      item.className = 'missing-price-item';
      item.innerHTML = `
        <span class="missing-price-name">${category}</span>
        <span>${formatCurrency(avgPurchase, 'PLN', true, 'extendedStats')} → ${formatCurrency(avgCurrent, 'PLN', true, 'extendedStats')} (${ratio}%)</span>
      `;
      categoryList.appendChild(item);
    }
  }

  generateActivityHeatmap();
}
function generateActivityHeatmap() {
	const heatmap = document.getElementById('activityHeatmap');
	if (!heatmap) return; 
	heatmap.innerHTML = '';
	const now = new Date();
	const months = [];
	for (let i = 11; i >= 0; i--) {
		const monthDate = new Date(now);
		monthDate.setMonth(now.getMonth() - i);
		months.push(monthDate);
	}
	const activityData = {};
	products.forEach(p => {
		const addedDate = new Date(p.dateAdded);
		const dateKey = addedDate.toISOString().split('T')[0];
		if (!activityData[dateKey]) {
			activityData[dateKey] = 0;
		}
		activityData[dateKey]++;
	});
	months.forEach(month => {
		const daysInMonth = new Date(month.getFullYear(), month.getMonth() + 1, 0).getDate();
		const monthName = month.toLocaleString('pl-PL', { month: 'short' });
		const monthHeader = document.createElement('div');
		monthHeader.style.gridColumn = 'span 7';
		monthHeader.style.textAlign = 'center';
		monthHeader.style.fontSize = '0.9rem';
		monthHeader.style.marginTop = '0.5rem';
		monthHeader.textContent = monthName;
		heatmap.appendChild(monthHeader);
		const firstDay = new Date(month.getFullYear(), month.getMonth(), 1).getDay();
		for (let i = 0; i < (firstDay === 0 ? 6 : firstDay - 1); i++) {
			const emptyDay = document.createElement('div');
			emptyDay.className = 'heatmap-day';
			heatmap.appendChild(emptyDay);
		}
		for (let day = 1; day <= daysInMonth; day++) {
			const dayDate = new Date(month.getFullYear(), month.getMonth(), day);
			const dateKey = dayDate.toISOString().split('T')[0];
			const count = activityData[dateKey] || 0;
			const dayElement = document.createElement('div');
			dayElement.className = count > 0 ? 'heatmap-day has-data' : 'heatmap-day';
			dayElement.style.opacity = count > 0 ? Math.min(0.3 + (count / 5), 1) : 0.3;
			if (count > 0) {
				const tooltip = document.createElement('div');
				tooltip.className = 'heatmap-tooltip';
				tooltip.textContent = `${count} produktów - ${dayDate.toLocaleDateString('pl-PL')}`;
				dayElement.appendChild(tooltip);
			}
			heatmap.appendChild(dayElement);
		}
	});
}
