async function updateBackgroundsFromGitHub() {
    const loader = new PokeballLoader('pokeballLoader').start(0);
    loader.update(10);

    try {
        const files = await fetchGitHubFiles('images/backgrounds');
        if (!files) {
            throw new Error('Brak plików tła na GitHub');
        }

        let addedCount = 0;
        let skippedCount = 0;
        let errorCount = 0;

        if (window.isUpdatingBackgrounds) {
            showNotification('Aktualizacja tła już trwa', 'warning');
            loader.reset();
            return;
        }

        window.isUpdatingBackgrounds = true;

        const imageExtensions = ['.png', '.jpg', '.jpeg', '.gif', '.bmp', '.webp', '.svg'];
        const imageFiles = files.filter(file => {
            const fileName = file.name.toLowerCase();
            return imageExtensions.some(ext => fileName.endsWith(ext));
        });

        for (let i = 0; i < imageFiles.length; i++) {
            const file = imageFiles[i];
            loader.update(10 + (i / imageFiles.length) * 80);

            let fileName = file.name.replace(/\.[^/.]+$/, "");
            fileName = fileName.replace(/_/g, ' ');

            const exists = backgroundImages.some(bg => {
                const existingName = bg.name.toLowerCase().trim().replace(/\s+/g, ' ');
                const newName = fileName.toLowerCase().trim().replace(/\s+/g, ' ');
                return existingName === newName && bg.source === 'github';
            });

            if (exists) {
                skippedCount++;
                continue;
            }

            try {
                backgroundImages.push({
                    id: generateUniqueId(),
                    name: fileName,
                    url: file.download_url,
                    dateAdded: new Date().toISOString(),
                    source: 'github'
                });
                addedCount++;
            } catch (fetchError) {
                errorCount++;
            }
        }

        if (addedCount > 0) {
            await saveBackgrounds();
            renderBackgrounds();
            updateBackgroundCount();
            playNotificationSound('success');
        } else if (skippedCount > 0) {
        } else {
        }

        loader.complete();
        setTimeout(() => {
            loader.reset();
            window.isUpdatingBackgrounds = false;
        }, 500);
    } catch (error) {
        loader.reset();
        window.isUpdatingBackgrounds = false;
        showNotification('Błąd podczas aktualizacji tła: ' + error.message, 'danger');
    }
}
