async function uploadToGallery() {
    logEvent(`Rozpoczęto przesyłanie zdjęć do galerii`);
    const loader = new PokeballLoader('pokeballLoader').start(0);
    loader.update(10);
    try {
        const input = document.getElementById('galleryUploadInput');
        if (!input.files || input.files.length === 0) {
            showNotification('Wybierz przynajmniej jedno zdjęcie', 'warning');
            loader.reset();
            return;
        }
        const files = Array.from(input.files);
        const skippedFiles = [];
        const addedFiles = [];
        for (let i = 0; i < files.length; i++) {
            const file = files[i];
            loader.update(10 + (i/files.length)*80);
            const name = document.getElementById('galleryImageName').value.trim() || file.name.replace(/\.[^/.]+$/, "");
            if (!isImageNameUnique(name)) {
                skippedFiles.push(file.name);
                continue;
            }
            const reader = new FileReader();
            const imageData = await new Promise((resolve) => {
                reader.onload = function(e) {
                    resolve(e.target.result);
                };
                reader.readAsDataURL(file);
            });
            galleryImages.push({
                id: generateUniqueId(),
                name: name,
                data: imageData,
                dateAdded: new Date().toISOString()
            });
            addedFiles.push(name);
        }
        if (skippedFiles.length > 0) {
            const filesList = skippedFiles.join(', ');
            showNotification(`Pominięto zdjęcia: ${filesList} - nazwa już istnieje`, 'warning', 5000);
        }
        if (addedFiles.length > 0) {
			logEvent(`Dodano ${addedFiles.length} zdjęć do galerii`);
            showNotification(`Dodano ${addedFiles.length} zdjęć do galerii`, 'success');
			playNotificationSound('success');
            await saveGallery();
            renderGallery();
        } else {
            showNotification('Nie dodano żadnych zdjęć', 'info');
        }
        input.value = '';
        document.getElementById('galleryImageName').value = '';
        document.getElementById('galleryUploadPanel').style.display = 'none';
        const uploadBtn = document.getElementById('galleryUploadBtn');
        uploadBtn.innerHTML = '<i class="fas fa-upload"></i> Dodaj zdjęcia';
        uploadBtn.className = 'btn-success';
        document.getElementById('gallerySelectBtn').style.display = 'inline-block';
        updateGalleryCount();
        loader.complete();
        setTimeout(() => loader.reset(), 1000);
    } catch (error) {
        loader.reset();
        showNotification(`Błąd podczas przetwarzania zdjęć: ${error.message}`, 'danger');
    }
    logEvent(`Dodano ${addedFiles.length} zdjęć do galerii`);
}
function deleteGalleryImage(index) {
    const imgName = galleryImages[index].name;
    logEvent(`Próba usunięcia zdjęcia z galerii: ${imgName}`);
    showConfirmation(
        'Potwierdzenie usunięcia',
        `Czy na pewno chcesz usunąć zdjęcie "${imgName}"? Ta operacja usunie również to zdjęcie z wszystkich produktów, które go używają.`,
        async function(confirmed) {
            if (!confirmed) return;
            const loader = new PokeballLoader('pokeballLoader').start(0);
            loader.update(30);
            try {
                galleryImages.splice(index, 1);
                loader.update(60);

                let updatedProducts = 0;
                products.forEach(product => {
                    if (product.image === galleryImages[index]?.data || 
                        (product.imageId && product.imageId === galleryImages[index]?.id)) {
                        delete product.image;
                        delete product.imageId;
                        updatedProducts++;
                    }
                });
                await saveGallery();
                await saveProducts();
                loader.update(80);
                renderGallery();
                renderProducts(applyAllFilters());
                loader.complete();
                setTimeout(() => {
                    loader.reset();
                    showNotification(`Usunięto zdjęcie i zaktualizowano ${updatedProducts} produktów`, 'success');
					playNotificationSound('success');
                    logEvent(`Usunięto zdjęcie z galerii: ${imgName}`);
                }, 500);
                updateGalleryCount();
            } catch (error) {
                loader.reset();
                showNotification('Błąd usuwania zdjęcia: ' + error.message, 'danger');
            }
        }
    );
}
function requestDeleteGalleryImage(index) {
    deleteGalleryImage(index);
}
async function deleteSelectedGalleryImages() {
    if (!selectedGalleryImages.length) {
        showNotification('Nie zaznaczono żadnych zdjęć', 'warning');
        return;
    }
    const count = selectedGalleryImages.length;
    showConfirmation(
        'Potwierdzenie usunięcia',
        `Czy na pewno chcesz usunąć ${count} zaznaczonych zdjęć? Ta operacja usunie również te zdjęcia z produktów, które ich używają.`,
        async function(confirmed) {
            if (!confirmed) return;
            const loader = new PokeballLoader('pokeballLoader').start(0);
            loader.update(10);
            try {
                const deletedImages = selectedGalleryImages.map(i => galleryImages[i]);
                selectedGalleryImages.sort((a, b) => b - a).forEach(i => {
                    galleryImages.splice(i, 1);
                });
                loader.update(30);
                let updatedProducts = 0;
                deletedImages.forEach(image => {
                    products.forEach(product => {
                        if (product.image === image.data || 
                            (product.imageId && product.imageId === image.id)) {

                            delete product.image;
                            delete product.imageId;
                            updatedProducts++;
                        }
                    });
                });
                loader.update(60);
                await saveGallery();
                await saveProducts();
                loader.update(80);
                selectedGalleryImages = [];
                galleryCurrentPage = 1;
                cancelGallerySelection();
                renderGallery();
                renderProducts(applyAllFilters());
                loader.complete();
                setTimeout(() => {
                    loader.reset();
                    showNotification(`Usunięto ${count} zdjęć i zaktualizowano ${updatedProducts} produktów`, 'success');
					playNotificationSound('success');
                }, 500);
                updateGalleryCount();
            } catch (error) {
                loader.reset();
                showNotification('Błąd usuwania zdjęć: ' + error.message, 'danger');
            }
        }
    );
}
