function sortGallery(criteria) {
	switch(criteria) {
		case 'name-asc':
			galleryImages.sort((a, b) => a.name.localeCompare(b.name));
			break;
		case 'name-desc':
			galleryImages.sort((a, b) => b.name.localeCompare(a.name));
			break;
		case 'date-asc':
			galleryImages.sort((a, b) => new Date(a.dateAdded) - new Date(b.dateAdded));
			break;
		case 'date-desc':
			galleryImages.sort((a, b) => new Date(b.dateAdded) - new Date(a.dateAdded));
			break;
		case 'size-asc':
			galleryImages.sort((a, b) => a.data.length - b.data.length);
			break;
		case 'size-desc':
			galleryImages.sort((a, b) => b.data.length - a.data.length);
			break;
	}
	renderGallery();
}
function formatFileSize(dataUrl) {
	const sizeInBytes = Math.floor(dataUrl.length * 0.75);
	if (sizeInBytes < 1024) {
		return sizeInBytes + ' B';
	} else if (sizeInBytes < 1024 * 1024) {
		return (sizeInBytes / 1024).toFixed(1) + ' KB';
	} else {
		return (sizeInBytes / (1024 * 1024)).toFixed(1) + ' MB';
	}
}
function renderGallery(selectMode = false) {
  const galleryContent = document.getElementById('galleryContent');
  galleryContent.innerHTML = '';
  galleryFilteredImages = [];
  galleryDisplayedIndexes = [];

  if (gallerySearchTerm) {
    galleryImages.forEach((img, index) => {
      if (img.name.toLowerCase().includes(gallerySearchTerm)) {
        galleryFilteredImages.push(img);
        galleryDisplayedIndexes.push(index);
      }
    });
  } else {
    galleryFilteredImages = [...galleryImages];
    galleryDisplayedIndexes = galleryImages.map((_, index) => index);
  }

  galleryTotalPages = Math.ceil(galleryFilteredImages.length / paginationConfig.galleryItemsPerPage);
  galleryCurrentPage = Math.min(galleryCurrentPage, galleryTotalPages);

  const startIndex = (galleryCurrentPage - 1) * paginationConfig.galleryItemsPerPage;
  const endIndex = Math.min(startIndex + paginationConfig.galleryItemsPerPage, galleryFilteredImages.length);
  const currentPageImages = galleryFilteredImages.slice(startIndex, endIndex);
  const currentPageIndexes = galleryDisplayedIndexes.slice(startIndex, endIndex);

  updateGalleryCount();

  if (galleryFilteredImages.length === 0) {
    galleryContent.innerHTML = `
      <div class="gallery-empty">
        <i class="fas fa-image"></i>
        <h3>${gallerySearchTerm ? 'Brak wyników' : 'Brak zdjęć'}</h3>
        <p>${gallerySearchTerm ? 'Spróbuj innej frazy' : 'Dodaj zdjęcia'}</p>
      </div>
    `;
    return;
  }

  currentPageImages.forEach((img, pageIndex) => {
    const originalIndex = currentPageIndexes[pageIndex];
    const item = document.createElement('div');
    item.className = 'gallery-item';

    const isGitHubImage = img.source === 'github';

    if (gallerySelectionMode) {
      if (!isGitHubImage) {
        item.classList.add('selectable');
      } else {
        item.classList.add('github-disabled');
      }
      if (selectedGalleryImages.includes(originalIndex)) {
        item.classList.add('selected');
      }
    }

    const showActionButtons = galleryOpenMode === 'manage' && !gallerySelectionMode && !isGitHubImage;
    item.setAttribute('data-index', originalIndex);

    const imageSrc = isGitHubImage ? img.url : img.data;

    item.innerHTML = `
      <div class="gallery-image-container">
        <div class="image-placeholder">
          <i class="fas fa-spinner"></i>
        </div>
        <img data-src="${imageSrc}" alt="${img.name}" class="lazy" 
             data-source="${img.source || 'local'}" 
             ${isGitHubImage ? 'data-github-url="' + img.url + '"' : ''}>
      </div>
      <span>
        ${img.name} 
      </span>
      <div class="image-info">
        ${isGitHubImage ? 'Online' : formatFileSize(img.data)}
        ${isGitHubImage ? '<i class="fa fa-globe" title="Obraz online" style="margin-left: 5px; color: #333; font-size: 0.8em;"></i>' : ''}
      </div>
      ${showActionButtons ? `
        <button class="delete-btn" title="Usuń zdjęcie">
          <i class="fas fa-times"></i>
        </button>
        <button class="edit-btn" title="Edytuj nazwę">
          <i class="fas fa-edit"></i>
        </button>
      ` : ''}
    `;

    if (gallerySelectionMode) {
      if (!isGitHubImage) {
        item.addEventListener('click', () => toggleGalleryImageSelection(originalIndex));
      }
    } else if (selectMode) {
      item.addEventListener('click', () => selectGalleryImage(originalIndex));
    } else {
      const imgElement = item.querySelector('img');
      imgElement.addEventListener('click', () => openGalleryImagePreview(originalIndex));

      if (showActionButtons) {
        const deleteBtn = item.querySelector('.delete-btn');
        deleteBtn.addEventListener('click', (e) => {
          e.stopPropagation();
          requestDeleteGalleryImage(originalIndex);
        });

        const editBtn = item.querySelector('.edit-btn');
        editBtn.addEventListener('click', (e) => {
          e.stopPropagation();
          editGalleryImageName(originalIndex);
        });
      }
    }

    galleryContent.appendChild(item);
  });

  renderPagination();
  setTimeout(initLazyLoading, 100);
}

