async function migrateToImageReferences() {
    logEvent('Rozpoczęcie migracji obrazów');
	const loader = new PokeballLoader('pokeballLoader').start(0);
	try {
		for (const product of products) {
			if (product.image && !product.imageId) {
				product.imageId = await saveImageToDB(product.image, 'productImages');
			}
		}
		for (const galleryImage of galleryImages) {
			if (galleryImage.data && !galleryImage.id) {
				const imageId = await saveImageToDB(galleryImage.data, 'galleryImages');
				galleryImage.id = imageId;
			}
		}
		localStorage.setItem('products', JSON.stringify(products));
		await saveGallery();
		loader.complete();
		setTimeout(() => loader.reset(), 500);
	} catch (error) {
		loader.reset();
		showNotification('Błąd migracji danych. Skontaktuj się z supportem.', 'danger');
	}
}
function initLazyLoading() {
  const lazyImages = document.querySelectorAll('img.lazy');
  const observer = new IntersectionObserver((entries, observer) => {
	entries.forEach(entry => {
	  if (entry.isIntersecting) {
		const img = entry.target;
		const originalSrc = img.dataset.src;
		img.src = originalSrc;
		img.onload = function() {
			img.classList.add('loaded');
			const placeholder = img.parentElement.querySelector('.image-placeholder');
			if (placeholder) {
				placeholder.style.display = 'none';
			}
		};
		img.onerror = function() {
			const placeholder = img.parentElement.querySelector('.image-placeholder');
			if (placeholder) {
				placeholder.innerHTML = '<i class="fas fa-times-circle"></i> Błąd ładowania';
			}
		};
		img.classList.remove('lazy');
		observer.unobserve(img);
	  }
	});
  });
  lazyImages.forEach(img => {
	observer.observe(img);
  });
}
function renderPagination() {
  const galleryContainer = document.getElementById('galleryModal').querySelector('.modal-content');
  const oldPagination = galleryContainer.querySelector('.pagination-container');
  if (oldPagination) oldPagination.remove();
  const paginationContainer = document.createElement('div');
  paginationContainer.className = 'pagination-container';
  paginationContainer.style.display = 'flex';
  paginationContainer.style.flexDirection = 'column';
  paginationContainer.style.alignItems = 'center';
  paginationContainer.style.marginTop = '1rem';
  paginationContainer.style.width = '100%';
  const buttonsContainer = document.createElement('div');
  buttonsContainer.className = 'pagination';
  const prevButton = document.createElement('button');
  prevButton.innerHTML = '<i class="fas fa-chevron-left" style="color: white;"></i>';
  prevButton.disabled = galleryCurrentPage === 1;
  prevButton.addEventListener('click', () => {
	if (galleryCurrentPage > 1) {
	  galleryCurrentPage--;
	  renderGallery();
	}
  });
  const nextButton = document.createElement('button');
  nextButton.innerHTML = '<i class="fas fa-chevron-right" style="color: white;"></i>';
  nextButton.disabled = galleryCurrentPage === galleryTotalPages;
  nextButton.addEventListener('click', () => {
	if (galleryCurrentPage < galleryTotalPages) {
	  galleryCurrentPage++;
	  renderGallery();
	}
  });
  buttonsContainer.appendChild(prevButton);
  buttonsContainer.appendChild(nextButton);
  const paginationInfo = document.createElement('div');
  paginationInfo.className = 'gallery-pagination-info';
  paginationInfo.textContent = `Strona ${galleryCurrentPage} z ${galleryTotalPages} • ${galleryFilteredImages.length} zdjęć`;
  paginationContainer.appendChild(buttonsContainer);
  paginationContainer.appendChild(paginationInfo);
  galleryContainer.appendChild(paginationContainer);
  setTimeout(initLazyLoading, 100);
}
