function loadProductImage(product) {
    if (product.imageSource === 'github' && product.imageId) {
        const galleryImage = galleryImages.find(img => img.id === product.imageId && img.source === 'github');
        return galleryImage ? galleryImage.url : (product.image || '');
    } else {
        return product.image || '';
    }
}
async function loadProductImagesFromDB() {
	try {
		const db = await openDB();
		const tx = db.transaction('productImages', 'readonly');
		const store = tx.objectStore('productImages');
		const request = store.getAll();
		return new Promise((resolve, reject) => {
			request.onsuccess = function() {
				const images = request.result || [];
				images.forEach(img => {
					const product = products.find(p => p.name === img.id);
					if (product) {
						product.image = img.data;
					}
				});
				resolve();
			};
			request.onerror = function() {
				reject('Błąd ładowania obrazów produktów');
			};
		});
	} catch (error) {
		logEvent('Błąd ładowania obrazów:', error);
	}
}
async function migrateProductImageReferences() {
	let needsMigration = false;
	products = products.map(product => {
		if (product.image && !product.imageId) {
			const galleryImage = galleryImages.find(img => img.data === product.image);
			if (galleryImage) {
				needsMigration = true;
				return {
					...product,
					imageId: galleryImage.id
				};
			}
		}
		return product;
	});
	if (needsMigration) {
		await saveProducts();
	}
}
