function checkIOSConnection() {
  return new Promise(resolve => {
    const xhr = new XMLHttpRequest();
    xhr.open('HEAD', 'https://www.google.com/favicon.ico?cache=' + Math.random(), true);
    xhr.timeout = 5000;
    xhr.onload = function() {
      resolve(true);
    };
    xhr.onerror = function() {
      resolve(false);
    };
    xhr.ontimeout = function() {
      resolve(false);
    };
    try {
      xhr.send();
    } catch (error) {
      resolve(false);
    }
  });
}
