async function updateShopsFromGitHub() {
    const loader = new PokeballLoader('pokeballLoader').start(0);
    loader.update(30);

    try {
        const githubShops = await fetchShopsFromGitHub();
        if (githubShops && githubShops.length > 0) {
            const existingShopIds = new Set(shops.map(shop => shop.id));
            const newShops = githubShops.filter(shop => !existingShopIds.has(shop.id));
            if (newShops.length > 0) {
                shops.push(...newShops);
                saveShops();
                renderShopsList();
                loader.complete();
                setTimeout(() => loader.reset(), 500);
                showNotification(`Dodano ${newShops.length} nowych sklepów`, 'success');
                playNotificationSound('success');
            } else {
                loader.complete();
                setTimeout(() => loader.reset(), 500);
            }
        } else {
            throw new Error('Nie udało się pobrać sklepów');
        }
    } catch (error) {
        loader.reset();
        showNotification('Błąd podczas aktualizacji sklepów: ' + error.message, 'danger');
    }
}

