(function() {
  const selectors = [
	'button',
	'.btn-success',
	'.btn-danger',
	'.btn-info',
	'.btn-warning',
	'.btn-primary',
	'.btn-shop',
	'.btn-gallery',
	'.btn-category',
	'.btn-stats',
	'.btn-instruction',
	'.btn-select',
	'.btn-update',
	'.custom-file-btn',
	'.edit-icon-btn',
	'.close-modal',
	'.hamburger',
	'.close-notification',
	'.delete-btn',
	'.edit-btn',
	'.btn-cardmarket',
	'.gallery-header-buttons button',
	'.gallery-sort-controls button',
	'.confirmation-buttons button',
	'.shop-item .actions button',
	'.category-item .actions button',
	'.add-shop-form button',
	'.add-currency-form button',
	'.add-category-form button',
	'.category-edit-form button',
	'.import-btn-wrapper'
  ].join(', ');

  function addShimmerToElement(el) {
	if (el.tagName === 'BUTTON' && el.closest('.import-btn-wrapper')) return;
	if (el.querySelector(':scope > .shimmer')) return;
	const oldChildren = Array.from(el.childNodes);
	const shimmer  = document.createElement('span');
	shimmer.className = 'shimmer';
	const textWrap = document.createElement('span');
	textWrap.className = 'text';
	textWrap.style.display = 'contents';
	oldChildren.forEach(n => textWrap.appendChild(n));
	const cs = getComputedStyle(el);
	if (cs.position === 'static') el.style.position = 'relative';
	if (cs.overflow !== 'hidden') el.style.overflow = 'hidden';
	el.insertBefore(shimmer, el.firstChild);
	el.appendChild(textWrap);
	shimmer.style.animationPlayState = 'paused';
	el.addEventListener('mouseenter', () => {
	  shimmer.style.animationPlayState = 'running';
	});
	el.addEventListener('mouseleave', () => {
	  shimmer.style.animationPlayState = 'paused';
	});
	el.addEventListener('focusin', () => {
	  shimmer.style.animationPlayState = 'running';
	});
	el.addEventListener('focusout', () => {
	  shimmer.style.animationPlayState = 'paused';
	});
  }
  function scanAll() {
	document.querySelectorAll(selectors).forEach(addShimmerToElement);
  }
  document.addEventListener('DOMContentLoaded', scanAll);
  scanAll();
  new MutationObserver(muts => {
	muts.forEach(m => {
	  if (m.addedNodes.length) scanAll();
	});
  }).observe(document.body, { childList: true, subtree: true });
})();
const img       = document.getElementById('card3dImage');
const container = document.getElementById('card3dContainer');
const card      = container.querySelector('.card');
const faces     = container.querySelectorAll('.card-face');
img.addEventListener('load', () => {
  const w = img.naturalWidth;
  const h = img.naturalHeight;
  [ container, card, ...faces ].forEach(el => {
    el.style.width  = w + 'px';
    el.style.height = h + 'px';
  });
  container.style.display = 'block';
});
document.addEventListener('DOMContentLoaded', function () {
  const container = document.querySelector('.card-container');
  const card = container.querySelector('.card');
  const cardShine = container.querySelector('.card__shine');
  const cardGlare = container.querySelector('.card__glare');
  let isInteracting = false;
  function updateCardVars(x, y) {
    const rect = container.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = x - centerX;
    const dy = y - centerY;
    const pointerFromLeft = (x - rect.left) / rect.width;
    const pointerFromTop = (y - rect.top) / rect.height;
    const pointerFromCenter = Math.min(Math.sqrt((dx / rect.width) ** 2 + (dy / rect.height) ** 2), 1);
    const rotateY = dx / 10;
    const rotateX = -dy / 10;
    card.style.setProperty('--rotate-x', `${rotateX}deg`);
    card.style.setProperty('--rotate-y', `${rotateY}deg`);
    card.style.setProperty('--pointer-from-center', pointerFromCenter);
    card.style.setProperty('--pointer-from-left', pointerFromLeft);
    card.style.setProperty('--pointer-from-top', pointerFromTop);
    cardShine.style.setProperty('--pointer-x', `${x}px`);
    cardShine.style.setProperty('--pointer-y', `${y}px`);
    cardGlare.style.setProperty('--pointer-x', `${pointerFromLeft * 100}%`);
    cardGlare.style.setProperty('--pointer-y', `${pointerFromTop * 100}%`);
  }
  function resetCardVars() {
    card.style.setProperty('--rotate-x', `0deg`);
    card.style.setProperty('--rotate-y', `0deg`);
  }
  container.addEventListener('mousemove', e => {
    isInteracting = true;
    updateCardVars(e.clientX, e.clientY);
  });
  container.addEventListener('mouseleave', () => {
    isInteracting = false;
    resetCardVars();
  });
  container.addEventListener('touchstart', e => {
    isInteracting = true;
    const t = e.touches[0];
    updateCardVars(t.clientX, t.clientY);
  }, { passive: false });
  container.addEventListener('touchmove', e => {
    if (!isInteracting) return;
    const t = e.touches[0];
    updateCardVars(t.clientX, t.clientY);
  }, { passive: false });
  container.addEventListener('touchend', () => {
    isInteracting = false;
    resetCardVars();
  });
  updateCardVars(window.innerWidth / 2, window.innerHeight / 2);
});
